mapper.insert:
        INSERT INTO sm_admin (
		    seq, email, tel, cell
		 ) values (
		    #{seq}, #{email}, #{tel}, #{cell}
		)

mapper.update:
UPDATE
  sm_user
SET
  name=#{name}
  , nickname=#{nickname}
  , grade_code=#{gradeCode}
  , status_code=#{statusCode}
  , mod_date=NOW()
WHERE
  seq=#{seq}

UPDATE
  sm_admin
SET
  email=#{email}
  , tel=#{tel}
  , cell=#{cell}
WHERE
  seq=#{seq}

mapper.insert:
INSERT INTO sm_admin_log (
	admin_seq, login_seq, grade_code, reg_date
) values (
	#{seq}, #{loginSeq}, #{gradeCode}, NOW()
)
