mapper.select:
SELECT A.seq, A.title, A.name, A.tel, A.cell, A.postcode, A.addr1, A.addr2, A.default_flag AS defaultFlag, A.reg_date AS regDate FROM sm_member_delivery A WHERE A.member_seq=#{memberSeq}
AND A.title LIKE CONCAT('%',#{findword},'%') AND A.name LIKE CONCAT('%',#{findword},'%')
ORDER BY A.seq DESC
