mapper.select:
SELECT seq, location, title, content AS content, order_no AS orderNo, mod_date AS modDate, cate_seq AS cateSeq, member_type_code AS memberTypeCode FROM sm_display WHERE member_type_code = #{memberTypeCode}
AND cate_seq=#{cateSeq}
AND cate_seq IS NULL
AND location = #{location}
AND title = #{title}

mapper.update:
	UPDATE sm_display SET
		content=#{content}
		, mod_date=NOW()
     WHERE member_type_code = #{memberTypeCode}



			 AND cate_seq=#{cateSeq}



			 AND cate_seq IS NULL



		AND location = #{location}


   		AND title = #{title}

mapper.insert:
INSERT INTO sm_display ( location, title, content, reg_date, member_type_code
, cate_seq
) VALUES ( #{location}, #{title}, #{content}, NOW(), #{memberTypeCode}
, #{cateSeq}
)

mapper.select:
SELECT i.sell_price AS sellPrice, i.market_price AS marketPrice, dl.seq, i.seq AS itemSeq, dl.order_no AS orderNo, i.name AS itemName, i.img1 AS img1, i.type_code AS typeCode, i.img2 AS img2, dl.display_seq AS displaySeq, i.origin_country AS originCountry, i.brand, CASE WHEN i.status_code = 'H' THEN '가승인' WHEN i.status_code = 'Y' THEN '판매중' WHEN i.status_code = 'N' THEN '판매중지' END AS statusFlag, di.limit_cnt AS limitCnt FROM sm_display_item di LEFT OUTER JOIN sm_display_item_list dl ON(di.seq = dl.display_seq) LEFT OUTER JOIN sm_item i ON(dl.item_seq = i.seq) WHERE di.member_type_code = #{memberTypeCode}
AND di.cate_seq=#{cateSeq}
AND di.cate_seq IS NULL
AND dl.display_seq=#{displaySeq}
AND i.status_code = #{statusFlag}
AND di.style_code=#{styleCode} ORDER BY dl.order_no ASC

SELECT seq , title AS listTitle , style_code AS styleCode , cate_seq AS cateSeq , member_type_code AS memberTypeCode , seq AS displaySeq FROM sm_display_item WHERE member_type_code = #{memberTypeCode}
AND cate_seq=#{cateSeq}
AND cate_seq IS NULL
AND style_code = #{styleCode}

mapper.update:
	UPDATE sm_display_item SET
		title=#{title}
     WHERE member_type_code = #{memberTypeCode}



			AND cate_seq=#{cateSeq}



			AND cate_seq IS NULL



	    AND style_code = #{styleCode}

mapper.select:
		SELECT name AS itemName
          FROM sm_item
         WHERE seq = #{itemSeq}

		SELECT item_seq AS itemSeq
          FROM sm_display_item_list

         WHERE display_seq = #{displaySeq}


           AND item_seq = #{itemSeq}

SELECT MAX(dl.order_no)
  FROM sm_display_item_list dl LEFT OUTER JOIN
       sm_display_item di ON (di.seq = dl.display_seq)
 WHERE di.member_type_code = #{memberTypeCode}



		 AND di.cate_seq=#{cateSeq}



		 AND di.cate_seq IS NULL



  	 AND dl.display_seq = #{displaySeq}

mapper.insert:
INSERT INTO sm_display_item_list (
	display_seq,
	item_seq,
	order_no
) VALUES (
	#{displaySeq},
	#{itemSeq},
	#{orderNo}
)

mapper.update:
	UPDATE sm_display_item_list SET
		order_no= #{orderNo}
     WHERE 1=1

	    AND display_seq = #{displaySeq}


   		AND item_seq = #{itemSeq}

mapper.insert:
INSERT INTO sm_display (
	location
	, title
	, content
	, order_no

		, cate_seq

	, member_type_code
	, reg_date
) VALUES (
	#{location}
	, #{title}
	, #{content}
	, #{orderNo}

		, #{cateSeq}

	, #{memberTypeCode}
	, NOW()
)

INSERT INTO sm_display_item ( member_type_code, style_code, order_no, title, limit_cnt
, cate_seq
) VALUES ( #{memberTypeCode}, #{styleCode}, #{orderNo}, #{title}, #{limitCnt}
, #{cateSeq}
)
