mapper.select:
SELECT
	COUNT(*) AS cnt
FROM
	sm_event
WHERE 1=1

	AND mall_seq = #{mallSeq}


	AND title LIKE CONCAT('%',#{findword},'%')



	AND status_code = #{statusCode}



	AND type_code = #{typeCode}


	AND lv1_seq = #{lv1Seq}


	AND seq = #{eventSeq}


	AND main_section = #{mainSection}


	AND e.end_date >= #{curDate}

SELECT e.seq, e.mall_seq AS mallSeq, (SELECT name FROM sm_user u WHERE e.mall_seq=u.seq) AS mallName, e.type_code AS typeCode, e.title, e.html, e.thumb_img AS thumbImg, e.reg_date AS regDate, e.end_date AS endDate, ic.cate_name AS cateName, e.coupon_seq AS couponSeq, e.show_flag AS showFlag, e.main_section AS mainSection, CASE WHEN e.status_code = 'H' THEN '대기중' WHEN e.status_code = 'Y' THEN '진행중' WHEN e.status_code = 'N' THEN '종료' END AS statusCode FROM sm_event e LEFT OUTER JOIN sm_item_category ic ON(e.lv1_seq = ic.seq) WHERE 1 = 1
AND e.mall_seq = #{mallSeq}
AND e.title LIKE CONCAT('%',#{findword},'%')
AND e.status_code = #{statusCode}
AND e.type_code = #{typeCode}
AND e.lv1_seq = #{lv1Seq}
AND e.seq = #{eventSeq}
AND e.main_section = #{mainSection}
AND e.end_date >= #{curDate}
ORDER BY e.seq ASC LIMIT ${pageNum * rowCount}, ${rowCount}

mapper.insert:
INSERT INTO sm_event (
	seq
	, mall_seq
	, type_code
	, status_code
	, title
	, html
	, thumb_img
	, end_date
	, reg_date

	, lv1_seq


	, show_flag

	, main_section
) VALUES (
	#{seq}
	, #{mallSeq}
	, #{typeCode}
	, #{statusCode}
	, #{title}
	, #{html}
	, #{thumbImg}
	, #{endDate, jdbcType=VARCHAR}
	, NOW()

	, #{lv1Seq}


	, #{showFlag}

	, #{mainSection}
)

mapper.update:
UPDATE sm_event SET
	title = #{title}
	, mall_seq = #{mallSeq}
	, html = #{html}

		, thumb_img = #{thumbImg}

	, end_date = #{endDate}
	, status_code = #{statusCode}
	, type_code = #{typeCode}

	, lv1_seq = #{lv1Seq}


	, coupon_seq = #{couponSeq}


	, show_flag = #{showFlag}

	, main_section = #{mainSection}
WHERE seq = #{seq}

mapper.select:
SELECT group_name AS groupName
  FROM sm_event_group
 WHERE seq = #{eventGroupSeq}

SELECT eg.seq AS eventGroupSeq , eg.event_seq AS eventSeq , eg.group_name AS groupName , eg.order_no AS orderNo , ei.order_no AS itemOrderNo , ei.group_seq AS groupSeq , ei.seq AS eventItemSeq , i.type_code AS itemTypeCode , i.seq AS itemSeq , i.name AS itemName , i.img1 AS img1 , i.img2 AS img2 , i.img3 AS img3 , i.img4 AS img4 , (SELECT MIN(option_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq) AS optionPrice , (SELECT MIN(sale_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq AND v.sale_price>0) AS salePrice , i.maker , (SELECT MAX(good_grade) FROM sm_item_review WHERE item_seq = ei.item_seq) AS reviewGrade , CASE WHEN i.status_code = 'H' THEN '가승인' WHEN i.status_code = 'Y' THEN '판매중' WHEN i.status_code = 'N' THEN '판매중지' END AS statusFlag , i.img_banner_code AS imgBannerCode ,CASE WHEN (SELECT MIN(sale_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq AND v.sale_price>0) > 0 THEN (SELECT MIN(sale_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq AND v.sale_price>0) ELSE (SELECT MIN(option_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq) END AS sellPrice ,(100-(100 * (SELECT MIN(sale_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq AND v.sale_price>0))/(SELECT MIN(option_price) FROM sm_item_option_value v LEFT OUTER JOIN sm_item_option o ON (o.seq=v.option_seq) WHERE o.item_seq=i.seq) ) AS salePercent FROM sm_event_group eg LEFT OUTER JOIN sm_event_item ei ON (eg.seq = ei.group_seq) LEFT OUTER JOIN sm_item i ON (ei.item_seq = i.seq) WHERE 1=1
AND i.status_code=#{statusCode}
AND eg.event_seq = #{seq} ORDER BY ei.order_no ASC

mapper.insert:
INSERT INTO sm_event_group (
	event_seq
	, group_name
	, order_no
	, mall_seq
) VALUES (
	#{eventSeq}
	, #{groupName}
	, #{orderNo}
	, #{mallSeq}
)

mapper.update:
UPDATE sm_event_group SET
	group_name = #{groupName}
	, order_no = #{orderNo}
WHERE seq = #{eventGroupSeq}
  AND  event_seq = #{eventSeq}

mapper.select:
SELECT COUNT(*) cnt
FROM sm_event_item

WHERE group_seq = #{eventGroupSeq}


	AND item_seq = #{itemSeq}

mapper.insert:
INSERT INTO sm_event_item (
	group_seq
	, item_seq
	, order_no
) VALUES (
	#{eventGroupSeq}
	, #{itemSeq}
	, #{orderNo}
)

mapper.update:
	UPDATE sm_event_item SET
		order_no= #{orderNo}
     WHERE 1=1

	    AND group_seq = #{groupSeq}


   		AND item_seq = #{itemSeq}

mapper.insert:
