mapper.select:
SELECT u.seq, u.id, u.name, c1.name AS statusText, m.open_date AS openDate, u.reg_date AS regDate FROM sm_user u LEFT OUTER JOIN sm_mall m ON (u.seq=m.seq) LEFT OUTER JOIN sm_common c1 ON (c1.group_code=18 AND u.status_code=c1.value) WHERE u.type_code='M'
AND u.status_code=#{statusCode}
ORDER BY u.seq DESC

mapper.insert:
INSERT INTO sm_mall (
	seq, url, pay_method, pg_code, pg_id, pg_key
) VALUES (
	#{seq}, #{url}, #{payMethod}, #{pgCode}, #{pgId}, #{pgKey}
)

mapper.update:
UPDATE sm_mall SET
  url=#{url}, pay_method=#{payMethod}, pg_code=#{pgCode}, pg_id=#{pgId}, pg_key=#{pgKey} 

  , open_date = NOW()


  , close_date = NOW()

WHERE seq=#{seq}
