SELECT d.seq, d.order_seq AS orderSeq, d.option_value_seq AS optionValueSeq, d.item_name AS itemName, i.seller_item_code AS sellerItemCode, d.item_seq AS itemSeq, d.option_value AS optionValue, d.status_code AS statusCode, d.sell_price AS sellPrice, d.org_price AS orgPrice, p.amount AS payPrice, ph.point, d.option_price AS optionPrice, d.supply_price AS supplyPrice, d.supply_master_price AS supplyMasterPrice, d.coupon_price AS couponPrice, d.order_cnt AS orderCnt, d.deli_cost AS deliCost, d.deli_prepaid_flag AS deliPrepaidFlag, d.deli_seq AS deliSeq, d.deli_no AS deliNo, d.box_count AS boxCnt, d.total_deli_cost AS totalDeliCost, (SELECT company_name FROM sm_deli_company WHERE seq=d.deli_seq) AS deliCompanyName, (SELECT track_url FROM sm_deli_company WHERE seq=d.deli_seq) AS deliTrackUrl, (SELECT complete_msg FROM sm_deli_company WHERE seq=d.deli_seq) AS completeMsg, d.tax_code AS taxCode, d.seller_seq AS sellerSeq, d.seller_master_seq AS sellerMasterSeq, d.seller_name AS sellerName, d.c10_date AS c10Date, d.c50_date AS c50Date, d.c55_date AS c55Date, d.c99_date AS c99Date, d.mod_date AS modDate, d.reg_date AS regDate, (SELECT img1 FROM sm_item WHERE seq=d.item_seq) img1, (SELECT name FROM sm_common WHERE group_code=6 AND value=d.status_code) statusText, (SELECT u.name FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallName, (SELECT u.id FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallId, (SELECT COUNT(*) FROM sm_order_detail WHERE order_seq=o.seq) AS orderCount, (SELECT COUNT(*) FROM sm_order_pay_cancel WHERE order_pay_seq=p.seq) AS partCancelCnt, p.tid, p.mid, p.approval_no AS approvalNo, IFNULL(p.pg_key, m.pg_key) AS pgKey, o.member_name AS memberName, o.receiver_name AS receiverName, (SELECT cate_name FROM sm_item_category WHERE seq = i.cate_lv1_seq) AS lv1Name, (SELECT cate_name FROM sm_item_category WHERE seq = i.cate_lv2_seq) AS lv2Name, (SELECT cate_name FROM sm_item_category WHERE seq = i.cate_lv3_seq) AS lv3Name, (SELECT cate_name FROM sm_item_category WHERE seq = i.cate_lv4_seq) AS lv4Name, p.pg_code AS pgCode, o.device_type AS deviceType, s.sales_name AS salesName, s.sales_tel AS salesTel, s.sales_cell AS salesCell, s.sales_email AS salesEmail, o.pay_method AS payMethod, c1.name AS payMethodName, c2.name AS taxName, (SELECT COUNT(*) FROM sm_estimate_compare WHERE order_seq=d.order_seq AND seller_seq=d.seller_seq) estimateCompareCnt, o.estimate_compare_flag AS estimateCompareFlag, c.member_type_code AS memberTypeCode, o.estimate_compare_flag AS estimateCompareFlag, c.dept_name AS deptName, g.name AS groupName, dc.company_name AS deliName, tr.request_flag AS requestFlag, ov.free_deli AS freeDeli, ov.event_added AS eventAdded FROM sm_order_detail d LEFT OUTER JOIN sm_order o ON (d.order_seq=o.seq) LEFT OUTER JOIN sm_item i ON (d.item_seq=i.seq) LEFT OUTER JOIN sm_item_option_value ov on (d.option_value_seq=ov.seq) LEFT OUTER JOIN sm_order_pay p ON (o.seq=p.order_seq) LEFT OUTER JOIN sm_point_history ph ON (o.seq=ph.order_seq AND ph.status_code='U') LEFT OUTER JOIN sm_mall m ON (o.mall_seq=m.seq) LEFT OUTER JOIN sm_user u ON (o.member_seq=u.seq) LEFT OUTER JOIN sm_member c ON (u.seq=c.seq) LEFT OUTER JOIN sm_member_group g ON (c.group_seq=g.seq) LEFT OUTER JOIN sm_seller s ON (d.seller_seq=s.seq) LEFT OUTER JOIN sm_deli_company dc ON (d.deli_seq=dc.seq) LEFT OUTER JOIN sm_common c1 ON (c1.group_code=21 AND o.pay_method=c1.value) LEFT OUTER JOIN sm_common c2 ON (c2.group_code=5 AND d.tax_code=c2.value) LEFT OUTER JOIN sm_order_tax_request tr ON (o.seq=tr.order_seq) WHERE
