SELECT o.seq AS orderSeq, o.total_price AS totalPrice, IFNULL(p.amount,o.pay_price) AS payPrice, (SELECT id FROM sm_user WHERE seq=o.member_seq) AS memberId, o.member_seq AS memberSeq, o.member_email AS memberEmail, o.receiver_name AS receiverName, o.receiver_postcode AS receiverPostcode, o.receiver_addr1 AS receiverAddr1, o.receiver_addr2 AS receiverAddr2, o.receiver_tel AS receiverTel, o.receiver_cell AS receiverCell, o.receiver_email AS receiverEmail, o.reg_date AS regDate, ph.point, o.request, (SELECT reason FROM sm_order_detail where seq=#{seq}) AS reason, o.member_name AS memberName, c.tel AS memberTel, IFNULL(c.cell, o.member_cell) AS memberCell, o.pay_method AS payMethod, c1.name AS payMethodName, (SELECT u.id FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallId, (SELECT u.name FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallName, o.estimate_compare_flag AS estimateCompareFlag, o.account_info AS accountInfo, c.group_seq AS memberGroupSeq, c.postcode, c.addr1, c.addr2, p.tid, o.check_bd AS checkBD FROM sm_order o LEFT OUTER JOIN sm_point_history ph ON (o.seq=ph.order_seq AND ph.status_code='U') LEFT OUTER JOIN sm_order_pay p ON (o.seq=p.order_seq) LEFT OUTER JOIN sm_user u ON (o.member_seq=u.seq) LEFT OUTER JOIN sm_member c ON (u.seq=c.seq) LEFT OUTER JOIN sm_common c1 ON (c1.group_code=21 AND o.pay_method=c1.value) WHERE o.seq=#{orderSeq}
AND 1=1
