(SELECT company_name FROM sm_deli_company WHERE seq=#{deliSeq}) AS deliCompanyName,
o.member_name AS memberName, c.tel AS memberTel, c.cell AS memberCell, o.pay_method AS payMethod, c1.name AS payMethodName, ov.free_deli AS freeDeli, ov.event_added AS eventAdded, (SELECT u.id FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallId, (SELECT u.name FROM sm_user u WHERE u.seq=o.mall_seq AND u.type_code='M') AS mallName FROM sm_order o LEFT OUTER JOIN sm_order_detail d ON (o.seq=d.order_seq) LEFT OUTER JOIN sm_item_option_value ov on (d.option_value_seq=ov.seq) LEFT OUTER JOIN sm_point_history p ON (o.seq=p.order_seq AND p.status_code='U') LEFT OUTER JOIN sm_user u ON (o.member_seq=u.seq) LEFT OUTER JOIN sm_member c ON (u.seq=c.seq) LEFT OUTER JOIN sm_common c1 ON (c1.group_code=21 AND o.pay_method=c1.value) WHERE 1=1 AND d.seq=#{seq}

SELECT d.seq, d.order_seq AS orderSeq, d.item_seq AS itemSeq, d.option_value_seq AS optionValueSeq, d.item_name AS itemName, d.option_value AS optionValue, d.status_code AS statusCode, d.sell_price AS sellPrice, d.org_price AS orgPrice, d.option_price AS optionPrice, d.supply_price AS supplyPrice, d.supply_master_price AS supplyMasterPrice, d.coupon_price AS couponPrice, d.order_cnt AS orderCnt, d.deli_cost AS deliCost, d.deli_prepaid_flag AS deliPrepaidFlag, d.deli_seq AS deliSeq, (SELECT company_name FROM sm_deli_company WHERE seq=d.deli_seq) AS deliCompanyName, (SELECT track_url FROM sm_deli_company WHERE seq=d.deli_seq) AS deliTrackUrl, d.deli_no AS deliNo, d.tax_code AS taxCode, d.seller_seq AS sellerSeq, d.seller_name AS sellerName, d.seller_master_seq AS sellerMasterSeq, d.seller_master_name AS sellerMasterName, d.c10_date AS c10Date, d.c30_date AS c30Date, d.c50_date AS c50Date, d.c55_date AS c55Date, d.c99_date AS c99Date, d.mod_date AS modDate, d.reg_date AS regDate, o.pay_price AS payPrice, o.point, c.cate_name AS cateLv1Name, i.img1, c1.name AS statusText, c2.name AS taxName, s.sales_tel AS salesTel, ov.free_deli AS freeDeli, ov.event_added AS eventAdded, i.type1 AS type1, i.type2 AS type2, i.type3 AS type3, i.maker AS maker, i.insurance_code AS insuranceCode, i.origin_country AS originCountry FROM sm_order_detail d LEFT OUTER JOIN sm_item_option_value ov on (d.option_value_seq=ov.seq) LEFT OUTER JOIN sm_order o ON(d.order_seq=o.seq) LEFT OUTER JOIN sm_item i ON(d.item_seq=i.seq) LEFT OUTER JOIN sm_item_category c ON (i.cate_lv1_seq=c.seq) LEFT OUTER JOIN sm_seller s ON (d.seller_seq=s.seq) LEFT OUTEr JOIN sm_common c1 ON (c1.group_code=6 AND d.status_code=c1.value) LEFT OUTER JOIN sm_common c2 ON (c2.group_code=5 AND d.tax_code=c2.value) WHERE d.order_seq=#{orderSeq}
AND d.seq=#{seq}
AND 1=1
AND d.seller_seq = #{loginSeq}
AND o.member_seq=#{loginSeq}
AND o.member_seq IS NULL
ORDER BY d.seq DESC

