	, d.box_count=#{boxCnt}, d.total_deli_cost=#{totalDeliCost}
WHERE
	d.status_code IN('20','30')
	AND d.seq=#{seq}


	AND d.seller_seq = #{sellerSeq}

UPDATE sm_order o SET o.np_pay_flag = #{npPayFlag}f
,o.np_pay_date = NOW()
WHERE o.seq = #{orderSeq}

