mapper.select:
SELECT d.status_code, COUNT(*) cnt FROM sm_order_detail d WHERE d.order_seq IN (SELECT seq FROM sm_order WHERE member_seq=#{loginSeq}) AND d.status_code IN ('00', '10', '20', '30', '40', '41', '42', '50', '90', '91', '99', '60', '61', '69', '70', '71', '79')
AND DATE_FORMAT(d.reg_date,'%Y-%m-%d') BETWEEN #{searchDate1} AND #{searchDate2}
GROUP BY d.status_code ORDER BY d.status_code

SELECT d.status_code AS statusCode , COUNT(*) AS orderCount , SUM(((IFNULL(d.sell_price,0) + IFNULL(d.option_price,0)) * d.order_cnt) + IFNULL(d.deli_cost,0)) AS sumPrice FROM sm_order_detail d WHERE DATE_FORMAT(d.reg_date, '%Y%m%d') = DATE_FORMAT(NOW(), '%Y%m%d') AND DATE_FORMAT(d.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND d.seller_seq = #{seq}
GROUP BY d.status_code ORDER BY d.status_code ASC

SELECT d.status_code AS statusCode , COUNT(*) AS orderCount , SUM(((IFNULL(d.sell_price,0) + IFNULL(d.option_price,0)) * d.order_cnt) + IFNULL(d.deli_cost,0)) AS sumPrice FROM sm_order_detail d WHERE d.reg_date BETWEEN DATE_FORMAT(DATE_ADD(NOW(), INTERVAL -WEEKDAY(NOW()) DAY), '%Y%m%d') AND DATE_FORMAT(DATE_ADD(DATE_FORMAT(DATE_ADD(NOW(), INTERVAL -WEEKDAY(NOW()) DAY), '%Y%m%d'), INTERVAL +6 DAY), '%Y%m%d') AND DATE_FORMAT(d.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND d.seller_seq = #{seq}
GROUP BY d.status_code ORDER BY d.status_code ASC

SELECT d.status_code AS StatusCode , COUNT(*) AS orderCount , SUM(((IFNULL(d.sell_price,0) + IFNULL(d.option_price,0)) * d.order_cnt) + IFNULL(d.deli_cost,0)) AS sumPrice FROM sm_order_detail d WHERE DATE_FORMAT(d.reg_date, '%Y%m') = DATE_FORMAT(NOW(), '%Y%m') AND DATE_FORMAT(d.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND d.seller_seq = #{seq}
GROUP BY d.status_code ORDER BY d.status_code ASC

SELECT d.status_code AS StatusCode , COUNT(*) AS orderCount , SUM(((IFNULL(d.sell_price,0) + IFNULL(d.option_price,0)) * d.order_cnt) + IFNULL(d.deli_cost,0)) AS sumPrice FROM sm_order_detail d WHERE DATE_FORMAT(d.reg_date, '%Y') = DATE_FORMAT(NOW(), '%Y') AND DATE_FORMAT(d.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND d.seller_seq = #{seq}
GROUP BY d.status_code ORDER BY d.status_code ASC

SELECT SUM(tt.sumPrice) AS sumPrice , SUM(tt.orderCount) AS orderCount FROM( SELECT SUM(t.sumPrice) AS sumPrice , 0 AS orderCount FROM ( SELECT ((IFNULL(od.sell_price,0) + IFNULL(od.option_price,0)) * od.order_cnt) AS sumPrice FROM sm_order_detail od WHERE 1=1 AND od.status_code >= '10' AND DATE_FORMAT(od.c10_date, '%Y-%m-%d' ) >= DATE_FORMAT( DATE_ADD( sysdate(), INTERVAL -7 DAY ), '%Y-%m-%d' ) AND DATE_FORMAT(od.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND od.seller_seq = #{seq}
UNION ALL
SELECT -((IFNULL(cod.sell_price,0) + IFNULL(cod.option_price,0)) * cod.order_cnt) AS sumPrice FROM sm_order_detail cod WHERE 1=1 AND cod.status_code IN('99') AND cod.c10_date IS NOT NULL AND DATE_FORMAT(cod.c99_date, '%Y-%m-%d' ) >= DATE_FORMAT( DATE_ADD( sysdate(), INTERVAL -7 DAY ), '%Y-%m-%d' ) AND DATE_FORMAT(cod.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND cod.seller_seq = #{seq}
) t UNION ALL
SELECT 0 AS sumPrice , COUNT(*) AS orderCount FROM sm_order_detail d WHERE 1=1 AND d.status_code >= '10' AND DATE_FORMAT( d.c10_date, '%Y-%m-%d' ) >= DATE_FORMAT( DATE_ADD( sysdate(), INTERVAL -7 DAY ), '%Y-%m-%d' ) AND DATE_FORMAT( d.reg_date, '%Y-%m-%d' ) >= DATE_FORMAT( '2015-12-05', '%Y-%m-%d' )
AND d.seller_seq = #{seq}
) tt

SELECT t.payDate AS payDate , SUM(t.sumPrice) AS sumPrice FROM( SELECT DATE_FORMAT(od.c10_date,'%Y%m%d') AS payDate , ((IFNULL(od.sell_price,0) + IFNULL(od.option_price,0)) * od.order_cnt) AS sumPrice FROM sm_order_detail od WHERE 1=1 AND od.status_code >= '10' AND DATE_FORMAT(od.c10_date, '%Y%m%d' ) > DATE_FORMAT( DATE_ADD( sysdate(), INTERVAL -7 DAY ), '%Y%m%d' )
AND od.seller_seq = #{loginSeq}
UNION ALL
SELECT DATE_FORMAT(cod.c99_date,'%Y%m%d') AS payDate , -((IFNULL(cod.sell_price,0) + IFNULL(cod.option_price,0)) * cod.order_cnt) AS sumPrice FROM sm_order_detail cod WHERE 1=1 AND cod.status_code IN('99') AND DATE_FORMAT(cod.c99_date, '%Y%m%d' ) > DATE_FORMAT( DATE_ADD( NOW(), INTERVAL -7 DAY ), '%Y%m%d' )
AND cod.seller_seq = #{loginSeq}
) t WHERE t.payDate = (SELECT DATE_FORMAT(DATE_ADD( NOW(), INTERVAL -#{weekPeriodCount} DAY ),'%Y%m%d') FROM dual) GROUP BY t.payDate ORDER BY t.payDate DESC

SELECT t.payDate AS payDate , SUM(t.sumPrice) AS sumPrice FROM( SELECT DATE_FORMAT(od.c10_date,'%Y%m%dhh24') AS payDate , ((IFNULL(od.sell_price,0) + IFNULL(od.option_price,0)) * od.order_cnt) AS sumPrice FROM sm_order_detail od WHERE 1=1 AND od.status_code >= '10' AND DATE_FORMAT(od.c10_date, '%Y%m%dhh24' ) = #{todayPeriodCount}
AND od.seller_seq = #{loginSeq}
UNION ALL
SELECT DATE_FORMAT(cod.c99_date,'%Y%m%dhh24') AS payDate , -((IFNULL(cod.sell_price,0) + IFNULL(cod.option_price,0)) * cod.order_cnt) AS sumPrice FROM sm_order_detail cod WHERE 1=1 AND cod.status_code IN('99') AND DATE_FORMAT(cod.c99_date, '%Y%m%dhh24' ) = #{todayPeriodCount}
AND cod.seller_seq = #{loginSeq}
) t GROUP BY t.payDate ORDER BY t.payDate DESC

