SELECT o.seq AS orderSeq, o.pay_method AS payMethod, c1.name AS payMethodName, o.total_price AS totalPrice, g.name AS groupName, o.member_name AS memberName, o.member_cell AS memberCell, o.member_email AS memberEmail, o.receiver_name AS receiverName, o.receiver_tel AS receiverTel, o.receiver_cell AS receiverCell, o.np_pay_flag AS npPayFlag, o.np_pay_date AS npPayDate, o.reg_date AS regDate,
(( SELECT SUM(((sell_price+option_price)*order_cnt)+(CASE WHEN deli_prepaid_flag = 'Y' THEN deli_cost ELSE 0 END)) FROM sm_order_detail WHERE status_code != '99' AND order_seq=d.order_seq ) - IFNULL(ph.point,0) ) AS payPrice, d.item_name AS itemName, d.option_value AS optionValue, d.sell_price AS sellPrice, d.org_price AS orgPrice, d.option_price AS optionPrice, d.order_cnt AS orderCnt, d.deli_prepaid_flag AS deliPrepaidFlag, CASE WHEN d.deli_prepaid_flag = 'Y' THEN d.deli_cost ELSE 0 END deliCost, d.status_code AS statusCode, c2.name AS statusText, c3.name AS taxName, o.receiver_postcode AS receiverPostcode, o.receiver_addr1 AS receiverAddr1, o.receiver_addr2 AS receiverAddr2, ov.free_deli AS freeDeli, ov.event_added AS eventAdded, (SELECT COUNT(*) FROM sm_order_detail WHERE order_seq=o.seq) AS orderCount FROM sm_order_detail d LEFT OUTER JOIN sm_item_option_value ov on (d.option_value_seq=ov.seq) LEFT OUTER JOIN sm_order o ON (d.order_seq=o.seq) LEFT OUTER JOIN sm_point_history ph ON (ph.status_code='U' AND o.seq=ph.order_seq) LEFT OUTER JOIN sm_order_pay p ON(o.seq=p.order_seq) LEFT OUTER JOIN sm_member m ON (o.member_seq=m.seq) LEFT OUTER JOIN sm_member_group g ON (m.group_seq=g.seq) LEFT OUTER JOIN sm_common c1 ON (c1.group_code=21 AND o.pay_method=c1.value) LEFT OUTER JOIN sm_common c2 ON (c2.group_code=6 AND d.status_code=c2.value) LEFT OUTER JOIN sm_common c3 ON (c3.group_code=5 AND d.tax_code=c3.value) WHERE o.old_order_id IS NULL AND (o.np_pay_flag = 'Y' OR (o.np_pay_flag = 'N' AND d.status_code != '99'))
AND o.member_seq=#{loginSeq}
AND DATE_FORMAT(o.reg_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{searchDate1}, '%Y-%m-%d') AND DATE_FORMAT(#{searchDate2}, '%Y-%m-%d')
AND DATE_FORMAT(o.np_pay_date, '%Y-%m-%d') BETWEEN DATE_FORMAT(#{npPayDate1}, '%Y-%m-%d') AND DATE_FORMAT(#{npPayDate2}, '%Y-%m-%d')
