(SELECT SUM((sell_price+option_price)*order_cnt) FROM sm_order_detail WHERE status_code != '99' AND order_seq=o.seq AND tax_code=2) AS taxFreeAmount,
((SELECT SUM(((sell_price+option_price)*order_cnt)+(CASE WHEN deli_prepaid_flag = 'Y' THEN deli_cost ELSE 0 END)) FROM sm_order_detail WHERE status_code != '99' AND order_seq=o.seq) - IFNULL(ph.point,0)) AS payPrice FROM sm_order o LEFT OUTER JOIN sm_member m ON (o.member_seq=m.seq) LEFT OUTER JOIN sm_point_history ph ON (ph.status_code='U' AND o.seq=ph.order_seq) WHERE o.np_pay_flag='N' AND o.seq=#{value}

